-- Table structure for `xx_log`
-- ----------------------------
DROP TABLE IF EXISTS `xx_log`;
CREATE TABLE `xx_log` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自动增长id',
  `create_date` datetime NOT NULL COMMENT '创建时间',
  `modify_date` datetime NOT NULL COMMENT '修改时间',
  `content` longtext COMMENT '内容',
  `ip` varchar(255) NOT NULL COMMENT '来源ip',
  `operation` varchar(255) NOT NULL COMMENT '操作对象',
  `operator` varchar(255) DEFAULT NULL COMMENT '操作员',
  `parameter` longtext COMMENT '参数',
  `level` smallint(6) NOT NULL COMMENT '错误级别debug<info<warn<error',
  `ipregion` varchar(128) DEFAULT NULL COMMENT 'ip地区',
  `logtype` varchar(26) NOT NULL COMMENT '日志类型',
  `url` varchar(1024) DEFAULT NULL COMMENT '访问url',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8 COMMENT='日志表';

