-- ----------------------------
-- Table structure for `xx_pageview`
-- ----------------------------
DROP TABLE IF EXISTS `xx_pageview`;
CREATE TABLE `xx_pageview` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `ip` varchar(64) NOT NULL COMMENT 'IP地址',
  `userid` int(11) DEFAULT NULL COMMENT '用户ID',
  `create_date` datetime NOT NULL COMMENT '创建时间',
  `modify_date` datetime NOT NULL COMMENT '修改时间',
  `useragent` varchar(255) DEFAULT NULL,
  `browsername` varchar(60) DEFAULT NULL,
  `spider_flag` tinyint(4) DEFAULT NULL COMMENT '爬虫标记 0 不是 1 是',
  `ip_region` varchar(60) DEFAULT NULL COMMENT 'ip物理地址',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COMMENT='访问量统计';

